pragma solidity ^0.8.20;

import "./UserSmartWallet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SmartWalletFactory
 * @notice Factory contract for creating deterministic user smart wallets
 * @dev Uses CREATE2 for predictable wallet addresses across chains
 */
contract SmartWalletFactory is Ownable {

    // State variables
    mapping(address => address) public userWallets;
    mapping(address => bool) public isValidWallet;
    mapping(address => address) public agentToUser; // Reverse mapping: agent => user
    address public immutable backendCoordinator;
    address public immutable usdcAddress;

    // Events
    event WalletCreated(address indexed user, address indexed wallet, bytes32 salt);
    event BackendCoordinatorUpdated(address indexed oldCoordinator, address indexed newCoordinator);

    // Errors
    error WalletAlreadyExists();
    error InvalidUser();
    error WalletCreationFailed();

    /**
     * @notice Constructor
     * @param _backendCoordinator Address of the backend coordinator
     * @param _usdcAddress Address of the USDC token for this chain
     * @param _owner Address of the contract owner
     */
    constructor(address _backendCoordinator, address _usdcAddress, address _owner) Ownable(_owner) {
        require(_backendCoordinator != address(0), "Invalid backend coordinator");
        require(_usdcAddress != address(0), "Invalid USDC address");
        backendCoordinator = _backendCoordinator;
        usdcAddress = _usdcAddress;
    }

    /**
     * @notice Create a smart wallet for a user
     * @param user Address of the user
     * @return wallet Address of the created smart wallet
     */
    function createWallet(address user) external returns (address wallet) {
        if (user == address(0)) revert InvalidUser();
        if (userWallets[user] != address(0)) revert WalletAlreadyExists();

        // Generate deterministic salt based on user address
        bytes32 salt = _generateSalt(user);

        // Deploy smart wallet using CREATE2
        wallet = address(new UserSmartWallet{salt: salt}(
            user,
            backendCoordinator,
            address(this),
            usdcAddress
        ));

        if (wallet == address(0)) revert WalletCreationFailed();

        // Store wallet mapping
        userWallets[user] = wallet;
        isValidWallet[wallet] = true;
        agentToUser[wallet] = user; // Store reverse mapping

        emit WalletCreated(user, wallet, salt);
    }

    /**
     * @notice Get the smart wallet address for a user
     * @param user Address of the user
     * @return wallet Address of the user's smart wallet (0x0 if doesn't exist)
     */
    function getWallet(address user) external view returns (address wallet) {
        return userWallets[user];
    }

    /**
     * @notice Predict the smart wallet address for a user (before creation)
     * @param user Address of the user
     * @return predictedAddress The predicted wallet address
     */
    function predictWalletAddress(address user) external view returns (address predictedAddress) {
        bytes32 salt = _generateSalt(user);

        bytes memory bytecode = abi.encodePacked(
            type(UserSmartWallet).creationCode,
            abi.encode(user, backendCoordinator, address(this), usdcAddress)
        );

        bytes32 hash = keccak256(abi.encodePacked(
            bytes1(0xff),
            address(this),
            salt,
            keccak256(bytecode)
        ));

        return address(uint160(uint256(hash)));
    }

    /**
     * @notice Check if a wallet exists for a user
     * @param user Address of the user
     * @return exists True if wallet exists
     */
    function hasWallet(address user) external view returns (bool exists) {
        return userWallets[user] != address(0);
    }

    /**
     * @notice Check if an address is a valid smart wallet created by this factory
     * @param wallet Address to check
     * @return valid True if it's a valid wallet
     */
    function isWalletValid(address wallet) external view returns (bool valid) {
        return isValidWallet[wallet];
    }

    /**
     * @notice Get the owner of a smart wallet
     * @param wallet Address of the smart wallet
     * @return owner Address of the wallet owner
     */
    function getWalletOwner(address wallet) external view returns (address owner) {
        if (!isValidWallet[wallet]) return address(0);
        return UserSmartWallet(payable(wallet)).owner();
    }

    /**
     * @notice Get the user address for a given agent wallet
     * @param agentWallet Address of the agent wallet
     * @return user Address of the user who owns this agent wallet
     */
    function getUserForAgent(address agentWallet) external view returns (address user) {
        return agentToUser[agentWallet];
    }

    /**
     * @notice Get total number of wallets created
     * @return count Total wallet count
     */
    function getTotalWallets() external view returns (uint256 count) {
        // This would require additional tracking in production
        // For now, we'll implement a basic counter
        return 0; // TODO: Implement counter
    }

    /**
     * @notice Emergency function to disable a compromised wallet
     * @param wallet Address of the wallet to disable
     * @dev Only owner can call this function
     */
    function disableWallet(address wallet) external onlyOwner {
        require(isValidWallet[wallet], "Wallet not valid");
        isValidWallet[wallet] = false;

        // Find and remove from userWallets mapping
        // This is gas-intensive but needed for security
        // In production, consider using a different approach
    }

    /**
     * @notice Create smart wallet for multiple users in batch
     * @param users Array of user addresses
     * @return wallets Array of created wallet addresses
     */
    function createWalletsBatch(address[] calldata users)
        external
        returns (address[] memory wallets)
    {
        uint256 length = users.length;
        require(length > 0 && length <= 100, "Invalid batch size"); // Limit batch size

        wallets = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            // Skip if wallet already exists
            if (userWallets[users[i]] != address(0)) {
                wallets[i] = userWallets[users[i]];
                continue;
            }

            wallets[i] = this.createWallet(users[i]);
        }
    }

    /**
     * @notice Generate deterministic salt for CREATE2
     * @param user Address of the user
     * @return salt The generated salt
     */
    function _generateSalt(address user) internal pure returns (bytes32 salt) {
        return keccak256(abi.encodePacked("CrossYield_SmartWallet_", user));
    }

    /**
     * @notice Get wallet creation bytecode hash (for verification)
     * @return hash The bytecode hash
     */
    function getWalletBytecodeHash() external pure returns (bytes32 hash) {
        return keccak256(type(UserSmartWallet).creationCode);
    }

    /**
     * @notice Get contract version
     * @return version The contract version
     */
    function version() external pure returns (string memory) {
        return "1.0.0";
    }
}","keccak256":"0x5db95f13b5c6f612a589266dc1a26874e8cf31f83a68521453bb7236386bfd56","license":"MIT"},"contracts/UserSmartWallet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/IProtocolAdapter.sol";

/**
 * @title Circle CCTP Interfaces
 * @notice Official Circle Cross-Chain Transfer Protocol interfaces
 */
interface ITokenMessenger {
    function depositForBurn(
        uint256 amount,
        uint32 destinationDomain,
        bytes32 mintRecipient,
        address burnToken
    ) external returns (uint64 nonce);
}

interface IMessageTransmitter {
    function receiveMessage(
        bytes memory message,
        bytes memory attestation
    ) external returns (bool success);
}

/**
 * @title UserSmartWallet
 * @notice Individual smart wallet for non-custodial USDC yield optimization
 * @dev Each user gets their own wallet with automated execution capabilities
 */
contract UserSmartWallet is ReentrancyGuard {

    // State variables
    address public immutable owner;
    address public immutable backendCoordinator;
    address public immutable factory;

    // USDC token address (will be set per chain)
    IERC20 public immutable USDC;

    // Wallet state
    bool public isActive;
    uint256 public totalDeposited;
    uint256 public totalWithdrawn;

    // Protocol allocations tracking
    mapping(string => uint256) public protocolBalances;
    mapping(address => uint256) public adapterBalances;
    string[] public activeProtocols;

    // CCTP Integration - Circle's official contract addresses
    mapping(uint256 => address) public tokenMessengerAddresses;
    mapping(uint256 => address) public messageTransmitterAddresses;
    mapping(uint256 => uint32) public cctpDomains;

    // CCTP Transfer State Management
    struct CCTPTransfer {
        uint64 nonce;
        uint256 amount;
        uint32 destinationDomain;
        address recipient;
        bytes32 messageHash;
        uint8 status; // 0=pending, 1=burned, 2=completed, 3=failed
        uint256 timestamp;
        uint256 retryCount;
        string destinationChain;
    }

    mapping(uint64 => CCTPTransfer) public cctpTransfers;
    mapping(bytes32 => bool) public processedMessages;
    uint64[] public activeCCTPTransfers;

    // Events
    event Deposited(address indexed user, uint256 amount, string strategy, uint256 timestamp);
    event Withdrawn(address indexed user, uint256 amount, uint256 timestamp);
    event EmergencyWithdrawal(address indexed user, uint256 amount, uint256 timestamp);
    event ProtocolAllocation(string indexed protocol, address adapter, uint256 amount, uint256 timestamp);
    event ProtocolWithdrawal(string indexed protocol, address adapter, uint256 amount, uint256 timestamp);
    event WalletDeactivated(uint256 timestamp);
    event BackendActionExecuted(string action, bytes data, uint256 timestamp);
    event AllocationExecuted(string[] protocolNames, address[] adapters, uint256[] amounts);

    // Enhanced CCTP Events
    event CCTPBurnExecuted(uint64 indexed nonce, uint256 amount, uint32 destinationDomain, address recipient, string destinationChain, uint256 timestamp);
    event CCTPMintCompleted(uint64 indexed nonce, bytes32 messageHash, uint256 amount, uint256 timestamp);
    event CCTPTransferFailed(uint64 indexed nonce, string reason, uint256 timestamp);
    event CCTPTransferCancelled(uint64 indexed nonce, uint256 timestamp);
    event CCTPConfigUpdated(uint256 chainId, address tokenMessenger, address messageTransmitter, uint32 domain);

    // Errors
    error OnlyOwner();
    error OnlyBackendOrOwner();
    error OnlyFactory();
    error WalletNotActive();
    error CCTPNotSupported();
    error InvalidDestinationDomain();
    error TransferNotFound();
    error TransferAlreadyCompleted();
    error MessageAlreadyProcessed();
    error CCTPMintFailed();
    error InvalidNonce();
    error TransferTooOld();
    error InsufficientBalance();
    error InvalidAmount();
    error InvalidProtocol();
    error TransferFailed();
    error ZeroAddress();

    // Modifiers
    modifier onlyOwner() {
        if (msg.sender != owner) revert OnlyOwner();
        _;
    }

    modifier onlyBackendOrOwner() {
        if (msg.sender != backendCoordinator && msg.sender != owner) revert OnlyBackendOrOwner();
        _;
    }

    modifier onlyFactory() {
        if (msg.sender != factory) revert OnlyFactory();
        _;
    }

    modifier onlyActive() {
        if (!isActive) revert WalletNotActive();
        _;
    }

    /**
     * @notice Constructor
     * @param _owner Address of the wallet owner (user)
     * @param _backendCoordinator Address of the backend coordinator
     * @param _factory Address of the factory contract
     * @param _usdcAddress Address of the USDC token for this chain
     */
    constructor(
        address _owner,
        address _backendCoordinator,
        address _factory,
        address _usdcAddress
    ) {
        if (_owner == address(0) || _backendCoordinator == address(0) || _factory == address(0) || _usdcAddress == address(0)) {
            revert ZeroAddress();
        }

        owner = _owner;
        backendCoordinator = _backendCoordinator;
        factory = _factory;
        USDC = IERC20(_usdcAddress);
        isActive = true;

        // Initialize CCTP addresses for current chain
        _initializeCCTPAddresses();
    }

    /**
     * @notice Initialize CCTP contract addresses for supported chains
     * @dev Uses Circle's official contract addresses
     */
    function _initializeCCTPAddresses() internal {
        // Ethereum Sepolia (Testnet)
        tokenMessengerAddresses[11155111] = 0x8FE6B999Dc680CcFDD5Bf7EB0974218be2542DAA;
        messageTransmitterAddresses[11155111] = 0xE737e5cEBEEBa77EFE34D4aa090756590b1CE275;
        cctpDomains[11155111] = 0;

        // Base Sepolia (Testnet)
        tokenMessengerAddresses[84532] = 0x8FE6B999Dc680CcFDD5Bf7EB0974218be2542DAA;
        messageTransmitterAddresses[84532] = 0xE737e5cEBEEBa77EFE34D4aa090756590b1CE275;
        cctpDomains[84532] = 6;

        // Arbitrum Sepolia (Testnet)
        tokenMessengerAddresses[421614] = 0x8FE6B999Dc680CcFDD5Bf7EB0974218be2542DAA;
        messageTransmitterAddresses[421614] = 0xE737e5cEBEEBa77EFE34D4aa090756590b1CE275;
        cctpDomains[421614] = 3;

        // Ethereum Mainnet
        tokenMessengerAddresses[1] = 0x28b5a0e9C621a5BadaA536219b3a228C8168cf5d;
        messageTransmitterAddresses[1] = 0x81D40F21F12A8F0E3252Bccb954D722d4c464B64;
        cctpDomains[1] = 0;

        // Base Mainnet
        tokenMessengerAddresses[8453] = 0x28b5a0e9C621a5BadaA536219b3a228C8168cf5d;
        messageTransmitterAddresses[8453] = 0x81D40F21F12A8F0E3252Bccb954D722d4c464B64;
